import { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import { groupAPI, productAPI } from '../../services/api';
import ProductCard from './ProductCard';
import JoinModal from './JoinModal';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [isJoining, setIsJoining] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      try {
        const data = await productAPI.getAllProducts();
        setProducts(data);
      } catch (error) {
        toast.error('Failed to load products');
        console.error(error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const filteredProducts = products.filter(product =>
    [product.name, product.category, product.seller?.shop_name]
      .filter(Boolean)
      .some(field => field.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleJoinGroup = (product) => {
    setSelectedProduct(product);
    setQuantity(1);
    setShowJoinModal(true);
  };

  const handleSubmitJoin = async () => {
    if (quantity < 1) {
      toast.error('Quantity must be at least 1');
      return;
    }
    setIsJoining(true);
    try {
      const existingGroup = await groupAPI.getGroupByProduct(selectedProduct.id);
      const groupId = existingGroup?.id || (await groupAPI.createGroup(selectedProduct.id)).id;
      await groupAPI.joinGroup(groupId, quantity);
      toast.success(
        `${existingGroup ? 'Joined existing' : 'Created new'} group for ${selectedProduct.name}`
      );
      setShowJoinModal(false);
    } catch (error) {
      toast.error(error?.response?.data?.detail || error.message || 'Failed to join group');
    } finally {
      setIsJoining(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader
        title="Browse Products"
        subtitle="Join groups to save up to 30% on local products"
        showBackButton={true}
        backButtonPath="/customer/dashboard"
      />

      {/* Search Bar */}
      <div className="mb-8">
        <div className="flex flex-col md:flex-row gap-4">
          <div className="relative flex-grow">
            <div className="absolute left-3 top-3 text-gray-400">
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"/>
              </svg>
            </div>
            <input
              type="text"
              placeholder="Search products by name, category, or seller..."
              className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500 bg-white shadow-sm"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {searchTerm && (
              <button
                className="absolute right-3 top-3 text-gray-400 hover:text-gray-600"
                onClick={() => setSearchTerm('')}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5"
                     viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd"
                        d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
                        clipRule="evenodd"/>
                </svg>
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Products Grid */}
      {isLoading ? (
        <div className="flex justify-center items-center py-12">
          <div className="text-center">
            <svg className="animate-spin h-10 w-10 mx-auto text-primary-500 mb-4"
                 fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10"
                      stroke="currentColor" strokeWidth="4"/>
              <path className="opacity-75" fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2
                       5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824
                       3 7.938l3-2.647z"/>
            </svg>
            <p className="text-gray-500">Loading products...</p>
            <p className="text-sm text-gray-400 mt-1">This may take a moment</p>
          </div>
        </div>
      ) : filteredProducts.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredProducts.map(product => (
            <ProductCard
              key={product.id}
              product={product}
              onJoin={() => handleJoinGroup(product)}
            />
          ))}
        </div>
      ) : (
        <div className="text-center py-16 bg-gray-50 rounded-lg">
          <svg className="h-16 w-16 mx-auto text-gray-400 mb-4" fill="none"
               viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                  d="M21 21l-6-6m2-5a7 7 0 11-14
                     0 7 7 0 0114 0z"/>
          </svg>
          <p className="text-gray-600 text-lg mb-2">No products found matching your search.</p>
          <p className="text-gray-500 mb-4">Try adjusting your search terms or browse all products.</p>
          <button
            onClick={() => setSearchTerm('')}
            className="text-white bg-primary-600 hover:bg-primary-700 px-6 py-2 rounded-lg transition-colors"
          >
            View All Products
          </button>
        </div>
      )}

      {/* Join Modal */}
      {showJoinModal && selectedProduct && (
        <JoinModal
          product={selectedProduct}
          quantity={quantity}
          setQuantity={setQuantity}
          isJoining={isJoining}
          onClose={() => setShowJoinModal(false)}
          onSubmit={handleSubmitJoin}
        />
      )}
    </div>
  );
};

export default Products;
