import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import PageHeader from '../../components/PageHeader';
import AddressModal from '../../components/AddressModal';
import { toast } from 'react-toastify';

const CustomerProfile = () => {
  const { user } = useAuth();
  const [showAddressModal, setShowAddressModal] = useState(false);
  const [customerAddress, setCustomerAddress] = useState(null);
  const [customerPhone, setCustomerPhone] = useState(null);

  const handleAddressSuccess = (data) => {
    setCustomerAddress(data.address);
    setCustomerPhone(data.phone);
    toast.success('Address saved successfully!');
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <PageHeader title="My Profile" />

      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Personal Information</h2>
          <button 
            className="text-primary-600 hover:text-primary-800 text-sm font-medium"
            onClick={() => {}}
          >
            Edit
          </button>
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <p className="text-sm text-gray-500 mb-1">Name</p>
            <p className="font-medium">{user?.name || 'Not provided'}</p>
          </div>
          <div>
            <p className="text-sm text-gray-500 mb-1">Email</p>
            <p className="font-medium">{user?.email || 'Not provided'}</p>
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Delivery Address</h2>
          <button 
            className="text-primary-600 hover:text-primary-800 text-sm font-medium"
            onClick={() => setShowAddressModal(true)}
          >
            {customerAddress ? 'Update' : 'Add'}
          </button>
        </div>

        {customerAddress ? (
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <p className="text-sm text-gray-500 mb-1">Address</p>
              <p className="font-medium">{customerAddress}</p>
            </div>
            <div>
              <p className="text-sm text-gray-500 mb-1">Phone</p>
              <p className="font-medium">{customerPhone}</p>
            </div>
          </div>
        ) : (
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-gray-500">No delivery address added yet</p>
            <button 
              className="mt-2 text-primary-600 hover:text-primary-800 text-sm font-medium"
              onClick={() => setShowAddressModal(true)}
            >
              Add Address
            </button>
          </div>
        )}
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Account Settings</h2>
        </div>

        <div className="space-y-4">
          <button className="w-full text-left py-3 px-4 rounded-lg hover:bg-gray-50 flex justify-between items-center">
            <span className="font-medium">Change Password</span>
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>

          <button className="w-full text-left py-3 px-4 rounded-lg hover:bg-gray-50 flex justify-between items-center">
            <span className="font-medium">Notification Preferences</span>
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>

          <button className="w-full text-left py-3 px-4 rounded-lg hover:bg-gray-50 flex justify-between items-center text-red-600 hover:text-red-800">
            <span className="font-medium">Delete Account</span>
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>
        </div>
      </div>

      {/* Address Modal */}
      <AddressModal 
        isOpen={showAddressModal}
        onClose={() => setShowAddressModal(false)}
        onSuccess={handleAddressSuccess}
      />
    </div>
  );
};

export default CustomerProfile;
