import { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import { groupAPI, ratingAPI } from '../../services/api';

const GroupStatus = () => {
  const [groups, setGroups] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('active');
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [showPickupModal, setShowPickupModal] = useState(false);
  const [pickupOTP, setPickupOTP] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [rating, setRating] = useState(5);
  const [feedback, setFeedback] = useState('');
  const [isSubmittingRating, setIsSubmittingRating] = useState(false);

  useEffect(() => {
    fetchGroups();
  }, []);

  // Function to fetch seller information for groups that don't have it
  const fetchSellerInfo = async (groups) => {
    try {
      // Create a map to store seller information by ID
      const sellerMap = {};

      // Process groups to identify which ones need seller info
      for (const group of groups) {
        if (!group.seller && group.seller_id) {
          // If we haven't already fetched this seller's info
          if (!sellerMap[group.seller_id]) {
            try {
              // This would require a new API endpoint to fetch seller by ID
              // For now, we'll create a placeholder seller object
              group.seller = {
                user_id: group.seller_id,
                shop_name: 'Shop #' + group.seller_id,
                address: 'Contact customer support for address',
                contact: 'Contact customer support for details'
              };
            } catch (error) {
              console.error(`Failed to fetch seller info for ID ${group.seller_id}:`, error);
            }
          } else {
            // Use the already fetched seller info
            group.seller = sellerMap[group.seller_id];
          }
        }
      }

      return groups;
    } catch (error) {
      console.error('Error fetching seller information:', error);
      return groups; // Return original groups if there's an error
    }
  };

  const fetchGroups = async () => {
    setIsLoading(true);
    try {
      const data = await groupAPI.getMyGroups();

      // Process groups to ensure they all have seller information
      const processedGroups = await fetchSellerInfo(data);

      setGroups(processedGroups);
    } catch (error) {
      toast.error('Failed to load groups');
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Not locked yet';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const handlePickup = (group) => {
    setSelectedGroup(group);
    setPickupOTP('');
    setShowPickupModal(true);
  };

  const handleVerifyPickup = async () => {
    if (!pickupOTP || pickupOTP.length !== 6) {
      toast.error('Please enter a valid 6-digit OTP');
      return;
    }

    setIsVerifying(true);
    try {
      await groupAPI.verifyPickup(selectedGroup.id, pickupOTP);
      toast.success('Pickup verified successfully!');
      setShowPickupModal(false);

      // Show rating modal after successful pickup
      setShowRatingModal(true);

      // Refresh groups
      fetchGroups();
    } catch (error) {
      toast.error(error.detail || 'Failed to verify pickup');
    } finally {
      setIsVerifying(false);
    }
  };

  const handleSubmitRating = async () => {
    setIsSubmittingRating(true);
    try {
      // Check if selectedGroup and selectedGroup.seller exist before accessing seller.id
      if (!selectedGroup || !selectedGroup.seller) {
        toast.error('Seller information is not available');
        return;
      }

      await ratingAPI.createRating({
        seller_id: selectedGroup.seller_id,
        score: rating,
        feedback: feedback
      });
      toast.success('Thank you for your feedback!');
      setShowRatingModal(false);
      setRating(5);
      setFeedback('');
      // Refresh the groups to update the UI
      fetchGroups();
    } catch (error) {
      toast.error(error.detail || 'Failed to submit rating');
    } finally {
      setIsSubmittingRating(false);
    }
  };

  const filteredGroups = groups.filter(group => {
    if (activeTab === 'active') return !group.locked_at;
    if (activeTab === 'locked') return group.locked_at && !group.is_picked_up;
    if (activeTab === 'completed') return group.is_picked_up;
    return true;
  });

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader
        title="My Groups"
        subtitle="View and manage your group purchases"
        showBackButton={true}
        backButtonPath="/customer/dashboard"
      />

      {/* Tabs */}
      <div className="border-b border-gray-200 mb-6">
        <nav className="-mb-px flex space-x-8">
          <button
            onClick={() => setActiveTab('active')}
            className={`pb-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'active' ? 'border-primary-500 text-primary-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Active
          </button>
          <button
            onClick={() => setActiveTab('locked')}
            className={`pb-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'locked' ? 'border-primary-500 text-primary-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Locked
          </button>
          <button
            onClick={() => setActiveTab('completed')}
            className={`pb-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'completed' ? 'border-primary-500 text-primary-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Completed
          </button>
        </nav>
      </div>

      {isLoading ? (
        <div className="text-center py-12">
          <p className="text-gray-500">Loading groups...</p>
        </div>
      ) : filteredGroups.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredGroups.map((group) => (
            <div key={group.id} className="bg-white rounded-lg shadow-md overflow-hidden">
              <div className="p-6">
                <div className="flex justify-between items-start mb-4">
                  <h2 className="text-lg font-semibold">{group.product.name}</h2>
                  <span className={`text-xs px-2 py-1 rounded-full ${group.is_picked_up ? 'bg-green-100 text-green-800' : group.locked_at ? 'bg-blue-100 text-blue-800' : 'bg-yellow-100 text-yellow-800'}`}>
                    {group.is_picked_up ? 'Completed' : group.locked_at ? 'Locked' : 'Active'}
                  </span>
                </div>

                <div className="space-y-2 text-sm text-gray-600 mb-4">
                  <div className="flex justify-between">
                    <span>Price:</span>
                    <span>₹{group.product.price} per {group.product.unit}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Your Quantity:</span>
                    <span>{group.quantity} {group.product.unit}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Your Total:</span>
                    <span className="font-medium">₹{(group.product.price * group.quantity).toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Group Total:</span>
                    <span className="font-medium">₹{group.total_price.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Members:</span>
                    <span>{group.members} / 10</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Status:</span>
                    <span>{group.locked_at ? `Locked on ${formatDate(group.locked_at)}` : 'Waiting for more members'}</span>
                  </div>
                </div>

                {group.locked_at && !group.is_picked_up && (
                  <div className="border-t border-gray-200 pt-4 mt-4">
                    <h3 className="font-medium mb-2">Pickup Information</h3>
                    {group.seller ? (
                      <div className="space-y-1 text-sm">
                        <p><span className="text-gray-600">Seller:</span> {group.seller.shop_name}</p>
                        <p><span className="text-gray-600">Address:</span> {group.seller.address}</p>
                        <p><span className="text-gray-600">Contact:</span> {group.seller.contact}</p>
                      </div>
                    ) : (
                      <div className="space-y-1 text-sm">
                        <p className="text-yellow-600">Seller information not available</p>
                      </div>
                    )}
                    <button
                      onClick={() => handlePickup(group)}
                      className="btn-primary w-full mt-4"
                    >
                      Verify Pickup with OTP
                    </button>
                  </div>
                )}

                {!group.locked_at && (
                  <div className="border-t border-gray-200 pt-4 mt-4">
                    <div className="bg-yellow-50 p-3 rounded-md">
                      <p className="text-sm text-yellow-800">
                        This group will lock automatically on Saturday if it reaches ₹1000 total value and has 10+ members.
                      </p>
                      <div className="mt-2 bg-gray-200 rounded-full h-2.5">
                        <div 
                          className="bg-primary-600 h-2.5 rounded-full" 
                          style={{ width: `${Math.min(100, (group.total_price / 1000) * 100)}%` }}
                        ></div>
                      </div>
                      <div className="flex justify-between text-xs text-gray-600 mt-1">
                        <span>₹{group.total_price.toFixed(2)}</span>
                        <span>₹1,000</span>
                      </div>
                    </div>
                  </div>
                )}

                {group.is_picked_up && (
                  <div className="border-t border-gray-200 pt-4 mt-4">
                    <div className="bg-green-50 p-3 rounded-md mb-3">
                      <p className="text-sm text-green-800 flex items-center">
                        <svg className="h-5 w-5 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                        </svg>
                        Picked up on {formatDate(group.picked_up_at)}
                      </p>
                    </div>
                    <button
                      onClick={() => {
                        setSelectedGroup(group);
                        setShowRatingModal(true);
                      }}
                      className="btn-primary w-full flex items-center justify-center"
                    >
                      <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
                      </svg>
                      Rate Seller
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-12 bg-white rounded-lg shadow-md">
          <p className="text-gray-500 mb-4">
            {activeTab === 'active' && 'You have no active groups. Join a product group to get started!'}
            {activeTab === 'locked' && 'You have no locked groups waiting for pickup.'}
            {activeTab === 'completed' && 'You have no completed pickups yet.'}
          </p>
          {activeTab === 'active' && (
            <a href="/customer/products" className="btn-primary">
              Browse Products
            </a>
          )}
        </div>
      )}

      {/* Pickup Verification Modal */}
      {showPickupModal && selectedGroup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">Verify Pickup</h2>
            <p className="mb-4 text-gray-600">
              Please ask the seller to provide you with the pickup OTP for {selectedGroup.product.name}.
            </p>
            <div className="mb-4">
              <label htmlFor="pickupOTP" className="block text-sm font-medium text-gray-700 mb-1">
                Enter 6-digit OTP
              </label>
              <input
                type="text"
                id="pickupOTP"
                maxLength="6"
                className="form-input text-center text-2xl tracking-widest"
                placeholder="------"
                value={pickupOTP}
                onChange={(e) => setPickupOTP(e.target.value.replace(/[^0-9]/g, ''))}
              />
            </div>
            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => setShowPickupModal(false)}
                className="btn-secondary"
                disabled={isVerifying}
              >
                Cancel
              </button>
              <button
                type="button"
                onClick={handleVerifyPickup}
                className="btn-primary"
                disabled={isVerifying}
              >
                {isVerifying ? 'Verifying...' : 'Verify Pickup'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Rating Modal */}
      {showRatingModal && selectedGroup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">Rate Your Experience</h2>
            <p className="mb-4 text-gray-600">
              How was your experience with {selectedGroup.seller ? selectedGroup.seller.shop_name : 'the seller'}?
            </p>
            <div className="mb-4">
              <div className="flex justify-center space-x-2 mb-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    type="button"
                    onClick={() => setRating(star)}
                    className="text-2xl focus:outline-none"
                  >
                    {star <= rating ? (
                      <span className="text-yellow-400">★</span>
                    ) : (
                      <span className="text-gray-300">★</span>
                    )}
                  </button>
                ))}
              </div>
              <p className="text-center text-sm text-gray-600 mb-4">
                {rating === 1 && 'Poor'}
                {rating === 2 && 'Fair'}
                {rating === 3 && 'Good'}
                {rating === 4 && 'Very Good'}
                {rating === 5 && 'Excellent'}
              </p>
              <label htmlFor="feedback" className="block text-sm font-medium text-gray-700 mb-1">
                Additional Feedback (Optional)
              </label>
              <textarea
                id="feedback"
                rows="3"
                className="form-input"
                placeholder="Share your experience..."
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></textarea>
            </div>
            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => setShowRatingModal(false)}
                className="btn-secondary"
                disabled={isSubmittingRating}
              >
                Skip
              </button>
              <button
                type="button"
                onClick={handleSubmitRating}
                className="btn-primary"
                disabled={isSubmittingRating}
              >
                {isSubmittingRating ? 'Submitting...' : 'Submit Rating'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GroupStatus;
