import { useState } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import { groupAPI } from '../../services/api';

const VerifyOTP = () => {
  const [groupId, setGroupId] = useState('');
  const [otp, setOtp] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationSuccess, setVerificationSuccess] = useState(false);
  const [groupDetails, setGroupDetails] = useState(null);

  const handleVerify = async (e) => {
    e.preventDefault();

    if (!groupId || !otp) {
      toast.error('Please enter both Group ID and OTP');
      return;
    }

    if (otp.length !== 6 || !/^\d+$/.test(otp)) {
      toast.error('OTP must be 6 digits');
      return;
    }

    setIsVerifying(true);
    try {
      // Verify the OTP with the backend
      const result = await groupAPI.verifyPickup(groupId, otp);
      setGroupDetails(result.group);
      setVerificationSuccess(true);
      toast.success('Pickup verified successfully!');
    } catch (error) {
      toast.error(error.detail || 'Verification failed. Please check Group ID and OTP.');
    } finally {
      setIsVerifying(false);
    }
  };

  const handleReset = () => {
    setGroupId('');
    setOtp('');
    setVerificationSuccess(false);
    setGroupDetails(null);
  };

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader
        title="Verify Pickup OTP"
        subtitle="Confirm customer pickup with OTP"
        showBackButton={true}
        backButtonPath="/seller/dashboard"
      />
      <div className="max-w-md mx-auto bg-white rounded-lg shadow-md p-6 mt-6">

        {!verificationSuccess ? (
          <form onSubmit={handleVerify} className="space-y-6">
            <div>
              <label htmlFor="groupId" className="form-label">
                Group ID
              </label>
              <input
                id="groupId"
                type="text"
                className="form-input"
                value={groupId}
                onChange={(e) => setGroupId(e.target.value)}
                placeholder="Enter group ID"
                required
              />
            </div>

            <div>
              <label htmlFor="otp" className="form-label">
                6-Digit OTP
              </label>
              <input
                id="otp"
                type="text"
                maxLength="6"
                className="form-input text-center text-2xl tracking-widest"
                value={otp}
                onChange={(e) => setOtp(e.target.value.replace(/[^0-9]/g, ''))}
                placeholder="------"
                required
              />
              <p className="mt-1 text-sm text-gray-500">
                Ask the customer to provide the OTP they received for pickup verification.
              </p>
            </div>

            <button
              type="submit"
              className="btn-primary w-full"
              disabled={isVerifying}
            >
              {isVerifying ? 'Verifying...' : 'Verify Pickup'}
            </button>
          </form>
        ) : (
          <div className="space-y-6">
            <div className="bg-green-50 p-4 rounded-md">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-green-800">Pickup verified successfully</h3>
                  <div className="mt-2 text-sm text-green-700">
                    <p>The customer has successfully picked up their order.</p>
                  </div>
                </div>
              </div>
            </div>

            {groupDetails && (
              <div className="border rounded-md p-4">
                <h3 className="font-medium text-gray-900 mb-2">Order Details</h3>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-500">Product:</span>
                    <span>{groupDetails.product.name}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-500">Quantity:</span>
                    <span>{groupDetails.total_quantity} {groupDetails.product.unit}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-500">Total Amount:</span>
                    <span>â‚¹{groupDetails.total_price.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-500">Customer:</span>
                    <span>{groupDetails.customer_name}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-500">Pickup Time:</span>
                    <span>{new Date().toLocaleString('en-IN')}</span>
                  </div>
                </div>
              </div>
            )}

            <button
              onClick={handleReset}
              className="btn-primary w-full"
            >
              Verify Another Pickup
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default VerifyOTP;
