import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import RatingCard from '../../components/RatingCard';
import { useAuth } from '../../context/AuthContext';
import { ratingAPI, productAPI } from '../../services/api';

const Reviews = () => {
  const { user } = useAuth();
  const [myRatings, setMyRatings] = useState([]);
  const [sellers, setSellers] = useState([]);
  const [selectedSeller, setSelectedSeller] = useState('');
  const [score, setScore] = useState(5);
  const [feedback, setFeedback] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        // Fetch user's ratings
        const ratingsData = await ratingAPI.getMyRatings();
        setMyRatings(ratingsData);

        // Fetch all products to get unique sellers
        const productsData = await productAPI.getAllProducts();
        const uniqueSellers = [];
        const sellerIds = new Set();

        productsData.forEach(product => {
          if (product.seller && !sellerIds.has(product.seller.user_id)) {
            sellerIds.add(product.seller.user_id);
            uniqueSellers.push(product.seller);
          }
        });

        setSellers(uniqueSellers);
      } catch (error) {
        toast.error('Failed to load ratings data');
        console.error(error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeller) {
      toast.error('Please select a seller to rate');
      return;
    }

    setIsSubmitting(true);
    try {
      await ratingAPI.createRating(selectedSeller, score, feedback);
      toast.success('Rating submitted successfully');

      // Reset form
      setSelectedSeller('');
      setScore(5);
      setFeedback('');

      // Refresh ratings
      const ratingsData = await ratingAPI.getMyRatings();
      setMyRatings(ratingsData);
    } catch (error) {
      toast.error(error.message || 'Failed to submit rating');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader
        title="Seller Reviews"
        subtitle="Rate sellers and view your previous ratings"
        showBackButton={true}
        backButtonPath="/customer/dashboard"
        className="p-6"
      />

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        {/* Left column - Submit new rating */}
        <div className="md:col-span-1">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Rate a Seller</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label htmlFor="seller" className="block text-sm font-medium text-gray-700 mb-2">
                  Select Seller
                </label>
                <select
                  id="seller"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  value={selectedSeller}
                  onChange={(e) => setSelectedSeller(e.target.value)}
                  disabled={isSubmitting}
                  required
                >
                  <option value="">-- Select a seller --</option>
                  {sellers.map((seller) => (
                    <option key={seller.user_id} value={seller.user_id}>
                      {seller.shop_name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Rating
                </label>
                <div className="flex items-center">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      type="button"
                      onClick={() => setScore(star)}
                      className="focus:outline-none"
                      disabled={isSubmitting}
                    >
                      <svg
                        className={`h-8 w-8 ${star <= score ? 'text-yellow-400' : 'text-gray-300'} transition-colors duration-150 hover:text-yellow-400`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                    </button>
                  ))}
                  <span className="ml-2 text-gray-600">{score}/5</span>
                </div>
              </div>

              <div className="mb-4">
                <label htmlFor="feedback" className="block text-sm font-medium text-gray-700 mb-2">
                  Feedback (Optional)
                </label>
                <textarea
                  id="feedback"
                  rows="4"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  placeholder="Share your experience with this seller"
                  value={feedback}
                  onChange={(e) => setFeedback(e.target.value)}
                  disabled={isSubmitting}
                ></textarea>
              </div>

              <button
                type="submit"
                className="w-full py-2 px-4 bg-primary-600 hover:bg-primary-700 text-white font-medium rounded-md shadow-sm hover:shadow-lg transition-all-standard flex items-center justify-center"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Submitting...
                  </>
                ) : (
                  'Submit Rating'
                )}
              </button>
            </form>
          </div>
        </div>

        {/* Right column - My ratings */}
        <div className="md:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">My Ratings</h2>

            {isLoading ? (
              <div className="flex justify-center items-center py-8">
                <svg className="animate-spin h-8 w-8 text-primary-600" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
              </div>
            ) : myRatings.length > 0 ? (
              <div className="space-y-4">
                {myRatings.map((rating) => (
                  <RatingCard key={rating.id} rating={rating} />
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <p className="text-gray-500">You haven't submitted any ratings yet.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reviews;
