import axios from 'axios';

// Set default base URL from the proxy in package.json
// This will be used in development. In production, you would set the actual API URL
// No need to set baseURL when using the proxy in development
// axios.defaults.baseURL = 'http://localhost:8000';

export default axios;

// Add a request interceptor for handling common headers, etc.
axios.interceptors.request.use(
  (config) => {
    // Get the token from localStorage if it exists
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add a response interceptor for handling common errors
axios.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // Handle 401 Unauthorized errors globally
    if (error.response && error.response.status === 401) {
      // Clear local storage and redirect to login
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }

    // Prevent 'not found' errors from showing as popups after login
    if (error.response && error.response.data && 
        (error.response.data.detail === 'Not found' || 
         error.response.data.detail === 'Group not found' || 
         error.response.data.detail && error.response.data.detail.includes('not found'))) {
      console.error('Resource not found:', error.response.data);
      // Return a resolved promise to prevent the error from propagating
      return Promise.resolve({ data: null, status: 'not_found' });
    }

    return Promise.reject(error);
  }
);
