import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import { ratingAPI, groupAPI } from '../../services/api';

const Reviews = () => {
  const [myRatings, setMyRatings] = useState([]);
  const [myGroups, setMyGroups] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedSeller, setSelectedSeller] = useState(null);
  const [rating, setRating] = useState(3);
  const [feedback, setFeedback] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        // Fetch user's ratings
        const ratingsData = await ratingAPI.getMyRatings();
        setMyRatings(ratingsData);

        // Fetch all user's groups to get all sellers the user has joined with
        const groupsData = await groupAPI.getMyGroups();
        setMyGroups(groupsData);
      } catch (error) {
        console.error('Error fetching reviews data:', error);
        toast.error('Failed to load reviews data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleRateClick = (seller) => {
    setSelectedSeller(seller);
    setRating(3); // Default rating
    setFeedback('');
    setShowRatingModal(true);
  };

  const handleSubmitRating = async () => {
    if (!selectedSeller) return;

    setIsSubmitting(true);
    try {
      await ratingAPI.createRating(selectedSeller.id, rating, feedback);
      toast.success('Rating submitted successfully!');

      // Refresh ratings
      const ratingsData = await ratingAPI.getMyRatings();
      setMyRatings(ratingsData);

      // Close modal
      setShowRatingModal(false);
    } catch (error) {
      console.error('Error submitting rating:', error);
      toast.error(error.detail || 'Failed to submit rating');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Get unique sellers from groups that can be rated
  const getUnratedSellers = () => {
    const ratedSellerIds = myRatings.map(rating => rating.seller_id);

    // Get unique sellers from groups
    const sellers = myGroups.reduce((acc, group) => {
      // Only include if not already rated
      if (!ratedSellerIds.includes(group.seller_id)) {
        // Check if seller already in accumulator
        const existingSeller = acc.find(s => s.id === group.seller_id);
        if (!existingSeller) {
          acc.push({
            id: group.seller_id,
            name: group.product.seller?.shop_name || 'Unknown Seller',
            group_id: group.id,
            product_name: group.product.name
          });
        }
      }
      return acc;
    }, []);

    return sellers;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader 
        title="My Reviews" 
        subtitle="Rate sellers and view your past reviews"
        showBackButton={true}
        backButtonPath="/customer/dashboard"
      />

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Left column - Sellers to rate */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Sellers to Rate</h2>

          {isLoading ? (
            <p className="text-center py-4">Loading...</p>
          ) : getUnratedSellers().length > 0 ? (
            <div className="space-y-4">
              {getUnratedSellers().map((seller) => (
                <div key={`${seller.id}-${seller.group_id}`} className="border rounded-lg p-4 hover:shadow-md transition-all duration-200">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-medium text-gray-900">{seller.name}</h3>
                      <p className="text-sm text-gray-600 mt-1">Product: {seller.product_name}</p>
                    </div>
                    <button
                      onClick={() => handleRateClick(seller)}
                      className="btn-primary text-sm px-3 py-1"
                    >
                      Rate Seller
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 bg-gray-50 rounded-lg">
              <svg className="h-12 w-12 mx-auto text-gray-400 mb-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <p className="text-gray-500">No sellers to rate at the moment.</p>
              <p className="text-sm text-gray-400 mt-1">Complete a purchase to rate a seller.</p>
            </div>
          )}
        </div>

        {/* Right column - My ratings */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">My Past Reviews</h2>

          {isLoading ? (
            <p className="text-center py-4">Loading...</p>
          ) : myRatings.length > 0 ? (
            <div className="space-y-6">
              {myRatings.map((rating) => {
                // Find seller name from groups if available
                const relatedGroup = myGroups.find(group => group.seller_id === rating.seller_id);
                const sellerName = relatedGroup?.product?.seller?.shop_name || 'Unknown Seller';

                return (
                <div key={rating.id} className="border-b pb-6 last:border-b-0 last:pb-0">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-medium text-gray-900 mb-1">{sellerName}</h3>
                      <div className="flex mb-1">
                        {[1, 2, 3, 4, 5].map((star) => (
                          <svg 
                            key={star} 
                            className={`h-5 w-5 ${star <= rating.score ? 'text-yellow-400' : 'text-gray-300'}`} 
                            fill="currentColor" 
                            viewBox="0 0 20 20"
                          >
                            <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118l-2.8-2.034c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                          </svg>
                        ))}
                      </div>
                      <p className="text-sm text-gray-500">
                        Rated on {formatDate(rating.created_at)}
                      </p>
                    </div>
                  </div>
                  {rating.feedback && (
                    <p className="mt-2 text-gray-600">{rating.feedback}</p>
                  )}
                </div>
              )})}
            </div>
          ) : (
            <div className="text-center py-8 bg-gray-50 rounded-lg">
              <svg className="h-12 w-12 mx-auto text-gray-400 mb-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
              </svg>
              <p className="text-gray-500">You haven't submitted any reviews yet.</p>
              <p className="text-sm text-gray-400 mt-1">Your reviews will appear here after you rate a seller.</p>
            </div>
          )}
        </div>
      </div>

      {/* Rating Modal */}
      {showRatingModal && selectedSeller && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">Rate {selectedSeller.name}</h2>
            <p className="mb-4 text-gray-600">
              How was your experience with this seller?
            </p>
            <div className="mb-4">
              <div className="flex justify-center space-x-2 mb-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    type="button"
                    onClick={() => setRating(star)}
                    className="text-2xl focus:outline-none"
                  >
                    {star <= rating ? (
                      <span className="text-yellow-400">★</span>
                    ) : (
                      <span className="text-gray-300">★</span>
                    )}
                  </button>
                ))}
              </div>
              <p className="text-center text-sm text-gray-600 mb-4">
                {rating === 1 && 'Poor'}
                {rating === 2 && 'Fair'}
                {rating === 3 && 'Good'}
                {rating === 4 && 'Very Good'}
                {rating === 5 && 'Excellent'}
              </p>
              <label htmlFor="feedback" className="block text-sm font-medium text-gray-700 mb-1">
                Additional Feedback (Optional)
              </label>
              <textarea
                id="feedback"
                rows="3"
                className="form-input"
                placeholder="Share your experience..."
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></textarea>
            </div>
            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => setShowRatingModal(false)}
                className="btn-secondary"
                disabled={isSubmitting}
              >
                Cancel
              </button>
              <button
                type="button"
                onClick={handleSubmitRating}
                className="btn-primary"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Submitting...' : 'Submit Rating'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Reviews;
