import { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../../components/PageHeader';
import { productAPI, groupAPI } from '../../services/api';

const OrderPlacement = () => {
  const [sellers, setSellers] = useState([]);
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [cart, setCart] = useState({});
  const [showOTPModal, setShowOTPModal] = useState(false);
  const [currentSeller, setCurrentSeller] = useState(null);
  const [otp, setOtp] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [otpError, setOtpError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      try {
        const data = await productAPI.getAllProducts();
        setProducts(data);

        // Group products by seller
        const sellerMap = {};
        data.forEach(product => {
          if (product.seller) {
            if (!sellerMap[product.seller.id]) {
              sellerMap[product.seller.id] = {
                id: product.seller.id,
                name: product.seller.shop_name,
                address: product.seller.address,
                contact: product.seller.phone,
                products: []
              };
            }
            sellerMap[product.seller.id].products.push(product);
          }
        });

        setSellers(Object.values(sellerMap));
      } catch (error) {
        toast.error('Failed to load products');
        console.error(error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleQuantityChange = (sellerId, productId, quantity) => {
    setCart(prevCart => {
      const newCart = { ...prevCart };
      if (!newCart[sellerId]) {
        newCart[sellerId] = {};
      }

      if (quantity > 0) {
        newCart[sellerId][productId] = quantity;
      } else {
        delete newCart[sellerId][productId];
        if (Object.keys(newCart[sellerId]).length === 0) {
          delete newCart[sellerId];
        }
      }

      return newCart;
    });
  };

  const calculateSellerTotal = (sellerId) => {
    if (!cart[sellerId]) return 0;

    return Object.entries(cart[sellerId]).reduce((total, [productId, quantity]) => {
      const product = products.find(p => p.id === parseInt(productId));
      return total + (product ? product.price * quantity : 0);
    }, 0);
  };

  const countSellerItems = (sellerId) => {
    return cart[sellerId] ? Object.keys(cart[sellerId]).length : 0;
  };

  const handleCheckout = (seller) => {
    setCurrentSeller(seller);
    setShowOTPModal(true);
    setOtp('');
    setOtpError('');
  };

  const handleGenerateOTP = async () => {
    try {
      // Create a group for each product in the cart for this seller
      const sellerProducts = Object.keys(cart[currentSeller.id]).map(productId => {
        return {
          productId: parseInt(productId),
          quantity: cart[currentSeller.id][productId]
        };
      });

      // For the first product, create a group and generate OTP
      if (sellerProducts.length > 0) {
        const firstProduct = sellerProducts[0];
        const newGroup = await groupAPI.createGroup(firstProduct.productId);
        await groupAPI.joinGroup(newGroup.id, firstProduct.quantity);

        // Generate OTP for this group
        try {
          await groupAPI.generatePickupOTP(newGroup.id);
          toast.success('OTP generated successfully');
        } catch (error) {
          toast.error('Failed to generate pickup OTP');
          console.error(error);
        }

        // For remaining products, create groups and join them
        for (let i = 1; i < sellerProducts.length; i++) {
          const product = sellerProducts[i];
          const group = await groupAPI.createGroup(product.productId);
          await groupAPI.joinGroup(group.id, product.quantity);
        }
      }
    } catch (error) {
      toast.error('Failed to create order');
      console.error(error);
    }
  };

  const handleVerifyOTP = async () => {
    if (!otp) {
      setOtpError('Please enter OTP');
      return;
    }

    setIsVerifying(true);
    try {
      // Verify OTP
      await groupAPI.verifyPickupOTP(otp);
      toast.success('Order verified successfully');

      // Clear cart for this seller
      setCart(prevCart => {
        const newCart = { ...prevCart };
        delete newCart[currentSeller.id];
        return newCart;
      });

      setShowOTPModal(false);
    } catch (error) {
      setOtpError('Invalid OTP. Please try again.');
    } finally {
      setIsVerifying(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 animate-fade-in">
      <PageHeader
        title="Place Order"
        subtitle="Purchase multiple products from sellers in a single transaction"
        showBackButton={true}
        backButtonPath="/customer/dashboard"
      />

      {isLoading ? (
        <div className="flex justify-center items-center py-12">
          <div className="text-center">
            <svg className="animate-spin h-10 w-10 mx-auto text-primary-500 mb-4" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            <p className="text-gray-500">Loading products...</p>
          </div>
        </div>
      ) : sellers.length > 0 ? (
        <div className="space-y-8">
          {sellers.map(seller => (
            <div key={seller.id} className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
              {/* Seller Name Row */}
              <div className="bg-gray-50 p-4 border-b border-gray-200">
                <h3 className="text-xl font-semibold text-gray-800">{seller.name}</h3>
              </div>

              {/* Seller Details Row */}
              <div className="p-4 bg-gray-100 border-b border-gray-200 flex flex-wrap gap-4">
                <div className="flex items-center">
                  <span className="text-gray-600 font-medium mr-2">Seller ID:</span>
                  <span>{seller.id}</span>
                </div>
                <div className="flex items-center">
                  <span className="text-gray-600 font-medium mr-2">Address:</span>
                  <span>{seller.address}</span>
                </div>
                <div className="flex items-center">
                  <span className="text-gray-600 font-medium mr-2">Contact:</span>
                  <span>{seller.contact}</span>
                </div>
              </div>

              {/* Products Table */}
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Product Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Quantity</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {seller.products.map(product => {
                      const quantity = cart[seller.id]?.[product.id] || 0;
                      return (
                        <tr key={product.id}>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="flex items-center">
                              <div className="h-10 w-10 flex-shrink-0 mr-3">
                                {product.image_url ? (
                                  <img className="h-10 w-10 rounded-full object-cover" src={product.image_url} alt={product.name} />
                                ) : (
                                  <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                                    <svg className="h-6 w-6 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                                    </svg>
                                  </div>
                                )}
                              </div>
                              <div>
                                <div className="text-sm font-medium text-gray-900">{product.name}</div>
                                <div className="text-sm text-gray-500">{product.unit}</div>
                              </div>
                            </div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-gray-900">₹{product.price.toFixed(2)}</div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="flex items-center border border-gray-300 rounded-md w-32">
                              <button 
                                onClick={() => handleQuantityChange(seller.id, product.id, Math.max(0, quantity - 1))}
                                className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-600 rounded-l-md"
                              >
                                -
                              </button>
                              <input 
                                type="number" 
                                min="0"
                                value={quantity}
                                onChange={(e) => handleQuantityChange(seller.id, product.id, Math.max(0, parseInt(e.target.value) || 0))}
                                className="w-12 text-center border-x border-gray-300 py-1 focus:outline-none"
                              />
                              <button 
                                onClick={() => handleQuantityChange(seller.id, product.id, quantity + 1)}
                                className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-600 rounded-r-md"
                              >
                                +
                              </button>
                            </div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-gray-900">₹{(product.price * quantity).toFixed(2)}</div>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                  <tfoot className="bg-gray-50">
                    <tr>
                      <td colSpan="2" className="px-6 py-4 text-sm text-gray-500">
                        <div className="flex items-center">
                          <span className="font-medium mr-2">Members:</span>
                          <span>{countSellerItems(seller.id)}</span>
                        </div>
                      </td>
                      <td className="px-6 py-4 text-right text-sm font-medium text-gray-900">Group Total:</td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-lg font-bold text-primary-600">₹{calculateSellerTotal(seller.id).toFixed(2)}</div>
                      </td>
                    </tr>
                  </tfoot>
                </table>
              </div>

              {/* Checkout Button */}
              <div className="p-4 bg-gray-50 border-t border-gray-200 flex justify-end">
                <button
                  onClick={() => handleCheckout(seller)}
                  disabled={!cart[seller.id] || Object.keys(cart[seller.id]).length === 0}
                  className="px-6 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                >
                  Checkout
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-16 bg-gray-50 rounded-lg">
          <svg className="h-16 w-16 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
          </svg>
          <p className="text-gray-600 text-lg mb-2">No sellers available</p>
          <p className="text-gray-500 mb-4">Please check back later for available products.</p>
        </div>
      )}

      {/* OTP Verification Modal */}
      {showOTPModal && currentSeller && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl max-w-md w-full">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-semibold">Verify Order</h3>
                <button 
                  onClick={() => setShowOTPModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <div className="mb-6 bg-gray-50 p-4 rounded-lg">
                <p className="text-gray-700 mb-2">Completing order with <span className="font-medium">{currentSeller.name}</span></p>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600">Total Items:</span>
                  <span className="font-medium">{countSellerItems(currentSeller.id)}</span>
                </div>
                <div className="flex justify-between text-sm mt-1">
                  <span className="text-gray-600">Total Amount:</span>
                  <span className="font-medium">₹{calculateSellerTotal(currentSeller.id).toFixed(2)}</span>
                </div>
              </div>

              <div className="mb-6">
                <button
                  onClick={handleGenerateOTP}
                  className="w-full bg-primary-600 text-white py-2 rounded-lg hover:bg-primary-700 transition-colors"
                >
                  Generate OTP
                </button>
              </div>

              <div className="mb-6">
                <label className="block text-gray-700 font-medium mb-2">Enter OTP:</label>
                <input 
                  type="text" 
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className={`w-full p-3 border ${otpError ? 'border-red-500' : 'border-gray-300'} rounded-lg focus:ring-primary-500 focus:border-primary-500`}
                  placeholder="Enter OTP sent to your phone"
                />
                {otpError && <p className="mt-1 text-sm text-red-600">{otpError}</p>}
              </div>

              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowOTPModal(false)}
                  className="px-5 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleVerifyOTP}
                  className="px-5 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:bg-gray-400 transition-colors flex items-center"
                  disabled={isVerifying}
                >
                  {isVerifying ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Verifying...
                    </>
                  ) : 'Verify OTP'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrderPlacement;
