import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { dealAPI } from '../../services/dealAPI';

const CustomerOrders = () => {
  const [customerId, setCustomerId] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Clear any existing error messages when component mounts or unmounts
  useEffect(() => {
    setError(null);
    toast.dismiss(); // Dismiss any existing toast notifications

    return () => {
      toast.dismiss(); // Dismiss any toast notifications when component unmounts
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!customerId || isNaN(customerId)) {
      toast.error('Please enter a valid customer ID');
      return;
    }

    setLoading(true);
    setError(null);
    setProducts([]);
    try {
      const customerProducts = await dealAPI.getCustomerProducts(customerId);
      setProducts(customerProducts);
      if (customerProducts.length === 0) {
        toast.info('No pending products found for this customer that belong to you as a seller');
      }
    } catch (err) {
      console.error('Error fetching customer products:', err);
      setError(err.detail || 'Failed to fetch customer products');
      toast.error(err.detail || 'Failed to fetch customer products');
    } finally {
      setLoading(false);
    }
  };

  const handleConfirmOrder = async (groupMemberId) => {
    try {
      await dealAPI.confirmOrder(groupMemberId);
      toast.success('Order confirmed successfully');

      // Remove the confirmed product from the list
      setProducts(products.filter(product => product.group_member_id !== groupMemberId));
    } catch (err) {
      toast.error(err.detail || 'Failed to confirm order');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold mb-6">Customer Order Confirmation</h1>

      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <form onSubmit={handleSubmit} className="flex flex-col md:flex-row gap-4">
          <div className="flex-grow">
            <label htmlFor="customerId" className="block text-sm font-medium text-gray-700 mb-1">
              Customer ID
            </label>
            <input
              type="text"
              id="customerId"
              value={customerId}
              onChange={(e) => setCustomerId(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter customer ID"
            />
          </div>
          <div className="self-end">
            <button
              type="submit"
              disabled={loading}
              className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? 'Loading...' : 'Search'}
            </button>
          </div>
        </form>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 relative">
          <span className="block sm:inline">{error}</span>
          <span className="absolute top-0 bottom-0 right-0 px-4 py-3" onClick={() => setError(null)}>
            <svg className="fill-current h-6 w-6 text-red-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
              <title>Close</title>
              <path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z"/>
            </svg>
          </span>
        </div>
      )}

      {products.length > 0 && (
        <div className="bg-white shadow-md rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Product
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Price
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Quantity
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Total
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Action
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {products.map((product) => (
                <tr key={`${product.id}-${product.group_id}`}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      {product.image_url && (
                        <div className="flex-shrink-0 h-10 w-10 mr-4">
                          <img
                            className="h-10 w-10 rounded-full object-cover"
                            src={product.image_url}
                            alt={product.name}
                          />
                        </div>
                      )}
                      <div>
                        <div className="text-sm font-medium text-gray-900">{product.name}</div>
                        <div className="text-sm text-gray-500">{product.unit}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    ₹{product.price.toFixed(2)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {product.quantity}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    ₹{(product.price * product.quantity).toFixed(2)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button
                      onClick={() => handleConfirmOrder(product.group_member_id)}
                      className="text-green-600 hover:text-green-900 bg-green-100 px-3 py-1 rounded-full"
                    >
                      Confirm Order
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {products.length === 0 && !loading && !error && (
        <div className="bg-gray-50 p-6 text-center rounded-lg">
          <p className="text-gray-500">No products to display. Enter a customer ID to view their pending orders.</p>
        </div>
      )}
    </div>
  );
};

export default CustomerOrders;
